/* global XMLHttpRequest */
const THREE = require('three')

const textureCache = {}
function loadTexture (texture, cb) {
  if (!textureCache[texture]) {
    let url = texture
    console.log(texture)
    if (texture.substring(0,2) === '0x') {
      console.log("Player skin or cape request")
      if (texture.substring(texture.length - 5) !== '-cape') {
        url = 'http://localhost:8911/skin?address=' + texture
      } else {
        url = 'http://localhost:8911/cape?id=1'
      }
    }
    textureCache[texture] = new THREE.TextureLoader().load(url)
  }
  cb(textureCache[texture])
}

function loadJSON (url, callback) {
  const xhr = new XMLHttpRequest()
  xhr.open('GET', url, true)
  xhr.responseType = 'json'
  xhr.onload = function () {
    const status = xhr.status
    if (status === 200) {
      callback(xhr.response)
    } else {
      throw new Error(url + ' not found')
    }
  }
  xhr.send()
}

module.exports = { loadTexture, loadJSON }
